mapper.select:
SELECT id, transaction_id, account, amount, transaction_time, description, status, gmt_creator, gmt_created, gmt_updated, gmt_updater FROM transaction WHERE id > 0
AND account LIKE CONCAT('%', #{account}, '%')
AND transaction_id = #{transactionId}
ORDER BY gmt_created DESC LIMIT #{offset}, #{limit}

select count(id) from transaction WHERE id > 0
AND account LIKE CONCAT('%', #{account}, '%')
AND transaction_id = #{transactionId}
